// Process & Split Jobs - Enhanced Data Processing
const internshalaData = $input.first();
const linkedinData = $input.last();

let allJobs = [];
let totalCount = 0;
let sources = [];
let errors = [];

console.log('=== JOB PROCESSING START ===');
console.log('Internshala Data:', internshalaData ? 'Present' : 'Missing');
console.log('LinkedIn Data:', linkedinData ? 'Present' : 'Missing');

// Enhanced function to safely extract jobs
function extractJobs(data, sourceName) {
  try {
    if (!data) {
      console.log(`${sourceName}: No data received`);
      errors.push(`${sourceName}: No data received`);
      return { jobs: [], count: 0, error: true };
    }

    let responseData = data.json || data;

    if (responseData.error || (responseData.success === false && responseData.message)) {
      console.log(`${sourceName}: API Error - ${responseData.message || responseData.error}`);
      errors.push(`${sourceName}: ${responseData.message || responseData.error}`);
      return { jobs: [], count: 0, error: true };
    }

    let jobsArray = [];
    if (responseData.jobs && Array.isArray(responseData.jobs)) {
      jobsArray = responseData.jobs;
    } else if (responseData.data && Array.isArray(responseData.data)) {
      jobsArray = responseData.data;
    } else if (Array.isArray(responseData)) {
      jobsArray = responseData;
    }

    if (jobsArray.length === 0) {
      console.log(`${sourceName}: No jobs found in response`);
      return { jobs: [], count: 0, error: false };
    }

    console.log(`${sourceName}: Successfully extracted ${jobsArray.length} jobs`);
    return { 
      jobs: jobsArray, 
      count: responseData.count || jobsArray.length, 
      error: false 
    };

  } catch (error) {
    console.error(`${sourceName}: Processing error - ${error.message}`);
    errors.push(`${sourceName}: Processing error - ${error.message}`);
    return { jobs: [], count: 0, error: true };
  }
}

// Process Internshala data
const internshalaResult = extractJobs(internshalaData, 'Internshala');
if (!internshalaResult.error && internshalaResult.jobs.length > 0) {
  const internshalaJobs = internshalaResult.jobs.map(job => ({
    title: job.title || job.jobTitle || 'Title Not Available',
    company: job.company || job.companyName || 'Company Not Available',
    location: job.location || job.jobLocation || 'Remote',
    stipend: job.stipend || job.salary || job.compensation || 'Not Specified',
    duration: job.duration || job.jobDuration || 'Not Specified',
    link: job.link || job.url || job.applyLink || '',
    source: 'Internshala',
    postedDate: job.postedDate || job.datePosted || 'Recently',
    hasJobs: true
  }));

  allJobs = allJobs.concat(internshalaJobs);
  totalCount += internshalaResult.count;
  sources.push(`Internshala (${internshalaResult.count})`);
}

// Process LinkedIn data
const linkedinResult = extractJobs(linkedinData, 'LinkedIn');
if (!linkedinResult.error && linkedinResult.jobs.length > 0) {
  const linkedinJobs = linkedinResult.jobs.map(job => ({
    title: job.title || job.jobTitle || 'Title Not Available',
    company: job.company || job.companyName || 'Company Not Available',
    location: job.location || job.jobLocation || 'Remote',
    stipend: job.stipend || job.salary || job.compensation || 'Not Specified',
    duration: job.duration || job.jobDuration || 'Not Specified',
    link: job.link || job.url || job.applyLink || '',
    source: 'LinkedIn',
    postedDate: job.postedDate || job.datePosted || 'Recently',
    hasJobs: true
  }));

  allJobs = allJobs.concat(linkedinJobs);
  totalCount += linkedinResult.count;
  sources.push(`LinkedIn (${linkedinResult.count})`);
}

console.log(`Total jobs before deduplication: ${allJobs.length}`);

// Enhanced deduplication
const uniqueJobs = allJobs.filter((job, index, self) => {
  return index === self.findIndex(j => {
    const titleMatch = j.title.toLowerCase().trim() === job.title.toLowerCase().trim();
    const companyMatch = j.company.toLowerCase().trim() === job.company.toLowerCase().trim();
    return titleMatch && companyMatch;
  });
});

console.log(`Unique jobs after deduplication: ${uniqueJobs.length}`);

// Return each job as a separate item for individual processing
if (uniqueJobs.length > 0) {
  return uniqueJobs.map((job, index) => ({
    json: {
      ...job,
      jobIndex: index + 1,
      totalJobs: uniqueJobs.length,
      sources: sources.join(', '),
      scrapedAt: new Date().toISOString(),
      scrapedAtLocal: new Date().toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' }),
      messageType: 'job_posting'
    }
  }));
} else {
  // Return a single item for no jobs case, with error details if any
  return [{
    json: {
      hasJobs: false,
      totalJobs: 0,
      sources: sources.length > 0 ? sources.join(', ') : 'All configured sources',
      errors: errors,
      hasErrors: errors.length > 0,
      scrapedAt: new Date().toISOString(),
      scrapedAtLocal: new Date().toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' }),
      message: errors.length > 0 ? 
        `Scan completed with issues: ${errors.join(', ')}` : 
        'Scan completed successfully - no new opportunities found',
      messageType: 'status_update'
    }
  }];
}

// Universal Message Formatter - Enhanced with Proper Markdown Escaping
const data = $json;

// Helper function to escape Markdown special characters
function escapeMarkdown(text) {
  if (!text || typeof text !== 'string') return '';
  return text.replace(/[_*[\]()~`>#+=|{}.!-]/g, '\\$&');
}

// Helper function to create clean markdown links
function createMarkdownLink(text, url) {
  if (!url || url.trim() === '') return text;
  const cleanText = escapeMarkdown(text);
  const cleanUrl = url.replace(/[()]/g, '\\$&');
  return `[${cleanText}](${cleanUrl})`;
}

if (data.messageType === 'job_posting' && data.hasJobs) {
  // Format Individual Job Message - Professional & Clean
  const job = data;

  // Job emojis for different categories
  const jobEmojis = {
    'web': 'üíª',
    'design': 'üé®',
    'marketing': 'üìà',
    'data': 'üìä',
    'content': '‚úçÔ∏è',
    'sales': 'üéØ',
    'hr': 'üë•',
    'finance': 'üí∞',
    'default': 'üöÄ'
  };

  // Get appropriate emoji based on job title
  function getJobEmoji(title) {
    const lowerTitle = title.toLowerCase();
    if (lowerTitle.includes('web') || lowerTitle.includes('developer') || lowerTitle.includes('programming')) return jobEmojis.web;
    if (lowerTitle.includes('design') || lowerTitle.includes('ui') || lowerTitle.includes('ux')) return jobEmojis.design;
    if (lowerTitle.includes('marketing') || lowerTitle.includes('seo') || lowerTitle.includes('social')) return jobEmojis.marketing;
    if (lowerTitle.includes('data') || lowerTitle.includes('analyst') || lowerTitle.includes('research')) return jobEmojis.data;
    if (lowerTitle.includes('content') || lowerTitle.includes('writer') || lowerTitle.includes('blog')) return jobEmojis.content;
    if (lowerTitle.includes('sales') || lowerTitle.includes('business')) return jobEmojis.sales;
    if (lowerTitle.includes('hr') || lowerTitle.includes('human')) return jobEmojis.hr;
    if (lowerTitle.includes('finance') || lowerTitle.includes('accounting')) return jobEmojis.finance;
    return jobEmojis.default;
  }

  // Create professional job message with proper escaping
  const emoji = getJobEmoji(job.title);
  let message = `${emoji} *${escapeMarkdown(job.title)}*\n\n`;

  // Company info
  message += `üè¢ *${escapeMarkdown(job.company)}*\n`;

  // Location (only if meaningful)
  if (job.location && job.location !== 'N/A' && job.location !== 'Not Specified' && job.location !== 'Remote') {
    message += `üìç ${escapeMarkdown(job.location)}\n`;
  } else {
    message += `üìç Remote Work\n`;
  }

  // Stipend (only if available)
  if (job.stipend && job.stipend !== 'N/A' && job.stipend !== 'Not Specified') {
    message += `üíµ ${escapeMarkdown(job.stipend)}\n`;
  }

  // Duration (only if available)
  if (job.duration && job.duration !== 'N/A' && job.duration !== 'Not Specified') {
    message += `‚è±Ô∏è ${escapeMarkdown(job.duration)}\n`;
  }

  // Source with appropriate icon
  const sourceIcon = job.source === 'LinkedIn' ? 'üîó' : 'üìã';
  message += `${sourceIcon} via ${escapeMarkdown(job.source)}\n\n`;

  // Call to action with proper link formatting
  if (job.link && job.link.trim() !== '') {
    message += `${createMarkdownLink('üéØ Apply Now', job.link)}\n\n`;
  } else {
    message += `üîç Search "${escapeMarkdown(job.title)}" at ${escapeMarkdown(job.company)}\n\n`;
  }

  // Professional closing without bot mention
  const closings = [
    'üí° _Apply within 24 hours for better visibility_',
    '‚ö° _Customize your application for this role_',
    'üöÄ _Follow up after applying for best results_',
    'üíº _Quality applications stand out_',
    'üéØ _Research the company before applying_'
  ];

  const randomClosing = closings[Math.floor(Math.random() * closings.length)];
  message += randomClosing;

  return {
    json: {
      text: message,
      chatId: '@RemoteInternIndia',
      jobTitle: job.title,
      company: job.company,
      source: job.source,
      messageType: 'job_posting'
    }
  };

} else {
  // Format No Jobs Message - Professional Status Update
  let message = `üîç *Daily Job Scan Complete*\n\n`;

  if (data.hasErrors && data.errors.length > 0) {
    message += `‚ö†Ô∏è *Status*: Partial scan completed\n`;
    message += `üìä *Checked*: ${escapeMarkdown(data.sources)}\n`;
    message += `‚è∞ *Time*: ${escapeMarkdown(data.scrapedAtLocal)}\n\n`;
    message += `üîß Some sources had technical issues\n`;
    message += `üîÑ Next scan: Tomorrow at 9:00 AM IST\n\n`;
    message += `üí™ _Working to resolve any issues_`;
  } else {
    message += `‚úÖ *Status*: All sources scanned successfully\n`;
    message += `üìä *Checked*: ${escapeMarkdown(data.sources)}\n`;
    message += `‚è∞ *Time*: ${escapeMarkdown(data.scrapedAtLocal)}\n\n`;
    message += `üìù No new remote internships today\n`;
    message += `üéØ You're not missing out!\n\n`;
    message += `‚è∞ Next update: Tomorrow at 9:00 AM IST\n`;
    message += `üí° _Best opportunities often post Monday-Thursday_`;
  }

  return {
    json: {
      text: message,
      chatId: '@RemoteInternIndia',
      messageType: 'status_update'
    }
  };
}
